import os.pathdic={}def buildmenu():      print('################')    print('歡迎進入成績系統')    print('################')        print('=>')    print('1. 建立成績')    print('2. 列出所有成績')    print('3. 成績查詢')    print('4. 離開系統')        os.pathif os.path.isfile('score.txt'):    print('file 存在')    z=open('score.txt','r')else:    print('file 不存在')    x=open('score.txt','w')    '''for row in x.readlines():        data=row.split(':')'''while True:    buildmenu()    sel=int(input('請輸入要選的選項'))    if sel==1:        while True:            name=input('請輸入名字 按0離開')             if name=='0':                break                print(dic)            score=float(input('請輸入分數'))            dic[name]=score            print()        elif sel==2:        print(dic)            elif sel==3:        c=input('請輸入要查詢誰的成績 (按０離開)')        if c==0:            break        elif c in dic:            print('他的成績是',dic[c])        else:            print('查無此人')                elif sel==4:        print('系統結束')        breaka=open('score.txt','w')for key,value in dic.items():    a.write(key)    a.write(':')    a.write(str(value))    a.write('\n')a.close()    